                                        if ($this.prop('checked')) {
                                            $applyOn.removeClass(da + 'Dn');
                                            $(functions.controllers.sliders).fadeIn();
                                        }
                                        else {
                                            $applyOn.addClass(da + 'Dn');
                                            $(functions.controllers.sliders).fadeOut();
                                        }
