                    evt.type === 'mouseover' ? $(this).css("background-color", hoverColor) : $(this).css("background-color", basicColor);
                    evt.type === 'mouseover' ? $(this).find('span').css("color", txtHoverColor) : $(this).find('span').css("color", txtBasicColor);

