                    let checked = '';
                    let hasClassDN = $component.hasClass(DeviceAlpha() + 'Dn');
                    if (!hasClassDN) {
                        comm.CreateOptionDOM(defaultComp, atr, targetVal);
                        checked = 'checked';
                    }
                    
                    //let random = comm.randomUnique.generate;
                    let random = comm.ran();
